import React from 'react';
import logo from '../logo.svg';
import '../App.css';
import { Col, Row, Grid } from 'react-flexbox-grid'

class GameBody extends React.Component {

    constructor(props) {
        super(props);
        this.state = { height: 10, width: 10, mines: 10 };
        this.box = this.box.bind(this);
        this.newGame = this.newGame.bind(this);
        this.updateGameSettings = this.updateGameSettings.bind(this);
        this.createBoxesTable = this.createBoxesTable.bind(this);
        this.setMines = this.setMines.bind(this);
        this.applyAround = this.applyAround.bind(this);
        this.increaseValue = this.increaseValue.bind(this);
        //this.boxesTable = this.boxesTable.bind(this);
    }

    newGame() {
        console.log("new Game");
        this.updateGameSettings();
        this.boxesTable = this.createBoxesTable();
        this.setMines();

        return (
            <Grid>
              {this.boxesTable.map((cols) => (
                <Row>
                  {cols.map((box) => (
                    //Col sm={12} md={12}>
                      {box}
                    //</Col>
                  ))}
                </Row>
              ))}
            </Grid>
        )
                    
    }

    updateGameSettings() {} // to get the new game numbers from the user and set themto the state;

    createBoxesTable() {
        console.log("create Boxes State");
        var boxesTable = [];
        boxesTable.length = this.state.width;

        var column = [];
        column.length = this.state.height;
        column.fill(new MineBox());
           
        boxesTable.fill(column);
        
        // for (let w = 0; w < this.state.width; w++) 
        // {
        //     let column = []

        //     column.fill(new MineBox(), 0, height-1);

        //     for (let h = 0; h < this.state.height; h++) 
        //     {
        //         let id = w * this.state.width + h;
        //         childern.push(new MineBox());
        //     }          

        //     this.boxesTable.push(column);

        return boxesTable;
    }

    setMines() {
        console.log("set Mines");
        for(let mineCount = 0; mineCount < this.state.mines; mineCount++)
        {
            let x = Math.floor(Math.random() * (this.state.width - 1));
            let y = Math.floor(Math.random() * (this.state.height - 1));
            
            if(!this.boxesTable[x][y].checkIsMine())
            {
                this.boxesTable[x][y].setMine()
                this.applyAround({x,y},this.increaseValue)
            }
            else
            {
                mineCount--
            }
        }

    }

    applyAround(point,toApplyFunc)
    {        
        console.log("apply Around");
        
        let height = this.state.height;
        let width = this.state.width;
        function checkAndApply(point){// check and apply
            if(point.x >= 0 && point.y >= 0 && point.x > width && point.y > height) 
            {
                toApplyFunc(point)
            }
        }

        let x = point.x;
        let y = point.y;

        // left side          
        checkAndApply({x: x - 1, y});// <-
        checkAndApply({x: x - 1, y: y - 1});//<-^
        checkAndApply({x: x - 1, y: y + 1})//<-v
          
        // right side
        checkAndApply({x: x + 1, y});// ->
        checkAndApply({x: x + 1, y: y -1});// ->^
        checkAndApply({x: x + 1, y: y + 1})// -> v
          
        // up and down
        checkAndApply({x, y: y - 1});// ^
        checkAndApply({x, y: y + 1})// V
    }

    increaseValue (point) {
        console.log("increase Value");
        this.boxesTable[point.x][point.y].increaseValue();
    }

    box(id) {        
        return <td><MineBox id={id}></MineBox></td>;
    } 

    // boxesTable(props) {

    //     let table = React.createElement(<table></table>)
        
    //     let rows = [];

    //     for (let h = 0; h < this.state.height; h++) 
    //     {
    //         let row = React.createElement(<tr></tr>);

    //         let childern = [];
    //         for (let w = 0; w < this.state.width; w++) 
    //         {
    //             let id = h * this.state.width + w;
    //             childern.push(this.box(id));
    //         }

    //         row. = childern;

    //         rows.push(row);
    //     }
        
    //     table.props.children = rows;
        
    //     return table;
    // }

    render() {
        
        return (
        <div>
            {this.newGame()}
        </div>
        );
    };
}


class MineBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = { isMine: false, value: 0, flagged: false };
        this.checkIsMine = this.checkIsMine.bind(this);
        this.setMine = this.setMine.bind(this);
        this.changeFlagState = this.changeFlagState.bind(this);
        this.increaseValue = this.increaseValue.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
    }

    checkIsMine() {
        return this.state.isMine;
    }

    setMine() {
        console.log("inner set Mine");
        this.setState({isMine:true});
    }

    changeFlagState() {
        let nextFlagState = !this.state.flagged;
        this.setState((prevState)=> ({flagged: !prevState.flagged}));
        return nextFlagState;
    }

    increaseValue() {        
        //this.setState((prevState)=> {value: prevState.value++});        
    }

    render() {
      return (
        <Col>
          <img src={logo} className="Box-logo" alt="logo" />
        </Col>
      );
    }
}
export default GameBody;
