import React from 'react';
import '../App.css';
import win from '../images/win.png';
import lose from '../images/lose.png';

class GameHeader extends React.Component {

    constructor(props) {
        super(props);
        this.state = {width: 10, height: 10, mines: 10}
        this.GameStatus = this.GameStatus.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
    }

    GameStatus () {
        let elem;
        var picMap ={
            win,
            lose
        }
        if (this.props.status == "win" || this.props.status == "lose")
        {
            elem = <div>
                <h2>You {this.props.status.toUpperCase()}! 
                <img src={picMap[this.props.status]} className={"Box-logo"} alt={this.props.status}/></h2>
            </div>;

        }
        else {
            elem = <div>
            <h2>Height: {this.props.height}  Width: {this.props.width}  Mines: {this.props.mines}</h2>
          </div>;
        }

        return elem;
    }

    StartNewGame () {
        return <div>
            <h3>Do you want to start a new game?</h3>
            <span>Board Width: <input type="number" value={this.state.width}></input> </span>
        </div>
       
    }

    render() {
      return (
        <div>
          <h2>Height: {this.props.height}  Width: {this.props.width}  Mines: {this.props.mines}</h2>
        </div>
      );
    }
  }

export default GameHeader;
