import React from 'react';
import logo from '../images/logo192.png';
import base from '../images/base.png';
import flag from '../images/flag.png';
import pic0 from '../images/0.png';
import pic1 from '../images/1.png';
import pic2 from '../images/2.png';
import pic3 from '../images/3.png';
import pic4 from '../images/4.png';
import pic5 from '../images/5.png';
import pic6 from '../images/6.png';
import pic7 from '../images/7.png';
import pic8 from '../images/8.png';
import '../App.css';
import { Col, Row, Grid } from 'react-flexbox-grid'

class GameBody extends React.Component {

    constructor(props) {
        super(props);
        this.state = { 
            height: 10, 
            width: 10, 
            mines: 10, 
            flags: {good: 0, bad: 0}, 
            displayData:"loading", 
            dataTable: []};

        this.newGame = this.newGame.bind(this);
        this.updateGameSettings = this.updateGameSettings.bind(this);
        this.createDataTable = this.createDataTable.bind(this);
        this.setMines = this.setMines.bind(this);
        this.applyAround = this.applyAround.bind(this);
        this.increaseValue = this.increaseValue.bind(this);
        this.onClick = this.onClick.bind(this);
        this.fixFlags = this.fixFlags.bind(this);
        this.formatBoxesTable = this.formatBoxesTable.bind(this);
        this.reveal = this.reveal.bind(this);
        
        //this.boxesTable = this.boxesTable.bind(this);
    }

    newGame() {
        console.log("new Game");
        this.updateGameSettings();
        this.dataTable = this.createDataTable()
        this.setMines();
        console.log(this.dataTable);
        this.setState({dataTable: this.dataTable});
        //this.boxesTable = this.createBoxesTable();

        // return (
        //     <div className="Mine-grid">
        //         {this.formatBoxesTable()}
        //     </div>
        // )
                    
    }
    
    updateGameSettings() {} // to get the new game numbers from the user and set themto the state;

    createDataTable() {
        console.log("create data State");

        var dataTable = Array.from({length: this.state.width}, () => Array.from({length: this.state.height}, () => ({value: 0, isMine: false, revealed:false, flagged:false})))

        return dataTable;
    }

    setMines() {
        console.log("set Mines");
        for(let mineCount = 0; mineCount < this.state.mines; mineCount++)
        {
            let x = Math.floor(Math.random() * (this.state.width - 1));
            let y = Math.floor(Math.random() * (this.state.height - 1));
            
            //if(!this.boxesTable[x][y].checkIsMine())
            if(!this.dataTable[x][y].isMine)            
            {
                //this.boxesTable[x][y].setMine()
                this.dataTable[x][y].isMine = true;
                
                this.applyAround({x,y},this.increaseValue)
            }
            else
            {
                mineCount--
            }
        }

        console.log(this.dataTable);

    }

    formatBoxesTable() {
        console.log(this.state.dataTable);
        if (this.state.dataTable.length == 0)
        {
            return "loading"
        }


        let rows = [];
        for (let height = 0; height < this.state.height; height++) {
            let cells = [];
            for (let width = 0; width < this.state.width; width++) {

                let elem = <Col key={width.toString() + "," + height.toString()}>
                    <MineBox id={width.toString() + "," + height.toString()} 
                             click={this.onClick} 
                             isMine={this.state.dataTable[width][height].isMine} 
                             value={this.state.dataTable[width][height].value}
                             revealed={this.state.dataTable[width][height].revealed}
                             flagged={this.state.dataTable[width][height].flagged}
                             ></MineBox>
                </Col> ;
                cells.push(elem);
            }
            let newRow = React.createElement(
                Row,
                {className: "Mine-row"},
                cells
            )
            rows.push(newRow)
        }

        let newTable = React.createElement(
            Grid,
            {className: "Mine-grid"},
            rows
        )

        return newTable;
    }

    applyAround(point,toApplyFunc)
    {        
        
        let height = this.state.height;
        let width = this.state.width;
        function checkAndApply(point){// check and apply
            console.log("check and apply");
            
            if(point.x >= 0 && point.y >= 0 && point.x < width && point.y < height) 
            {
                toApplyFunc(point)
            }
        }

        let x = point.x;
        let y = point.y;

        // left side          
        checkAndApply({x: x - 1, y});// <-
        checkAndApply({x: x - 1, y: y - 1});//<-^
        checkAndApply({x: x - 1, y: y + 1})//<-v
          
        // right side
        checkAndApply({x: x + 1, y});// ->
        checkAndApply({x: x + 1, y: y -1});// ->^
        checkAndApply({x: x + 1, y: y + 1})// -> v
          
        // up and down
        checkAndApply({x, y: y - 1});// ^
        checkAndApply({x, y: y + 1})// V
    }

    increaseValue (point) {
        this.dataTable[point.x][point.y].value++;
    }

    onClick (shift, box) {
        console.log("on click");
        if (shift)
        {
            this.fixFlags(box)
        }
        else
        {
            if (box.isMine)
            {
                this.gameOver()
            }

            let id = this.convertId(box.id)
            this.reveal(id)
        }
        

    }

    fixFlags(box){
        
        let {x,y} = this.convertId(box.id);
        let currBox = this.state.dataTable[x][y];

        if (this.state.flags.bad + this.state.flags.good == this.state.mines && !currBox.flagged)
        {
            alert("You'r out of flags")
            return;
        }

        var goodOrBad = currBox.isMine ? "good" : "bad";
        var change = currBox.flagged ? -1 : 1;
        
        
        let {flags} = this.state;
        console.log(flags);
        flags[goodOrBad] += change;
        this.setState(flags, ()=>{
            if (this.state.flags.good == this.state.mines)
            {
                alert("You Win!")
            }
        })
        
        let tempGrid = this.state.dataTable;
        tempGrid[x][y].flagged = !tempGrid[x][y].flagged;
        this.setState({dataTable: tempGrid});
    }

    reveal(id){
        let {x,y} = id;
        //this.boxesTable[x][y].type.prototype.reveal();
        let tempGrid = this.state.dataTable;
        if (!tempGrid[x][y].revealed)
        {
            tempGrid[x][y].revealed = true;
            console.log("revealed:" + x + "," + y);
            this.setState({dataTable: tempGrid});

            if (tempGrid[x][y].value == 0)
            {
                this.applyAround(id, this.reveal)
            }
        }

    }

    convertId(id) { 
        if (typeof id == "string")
        {
            let [x,y]= id.split(",");
            return {x,y};
        }
        return id;
    };// to fix if on opposite case arises

    gameOver(){
        alert("GAME OVER!!!")
    }

    render() {
        
        return (
        <div>
            <button onClick={this.newGame}>new game</button>
            {this.formatBoxesTable()}
        </div>
        );
    };
}


class MineBox extends React.Component {

    constructor(props) {
        super(props);
        
        this.checkIsMine = this.checkIsMine.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.picMap = {
            0: pic0,
            1: pic1,
            2: pic2,
            3: pic3,
            4: pic4,
            5: pic5,
            6: pic6,
            7: pic7,
            8: pic8,
            flag: flag,
            base: base
        }
        
        // this.handleSubmit = this.handleSubmit.bind(this);
    }

    checkIsMine() {
        return this.props.isMine;
    }
   

    handleClick (e) {
        const isShift = e.shiftKey;
        console.log(e.target);
        

        let boxData = {
            isMine: this.props.isMine,
            value: this.props.value,
            flag: this.props.flagged,
            id: this.props.id
        }

        this.props.click(isShift, boxData);

    }

    render() {
      return (
        
          <img src={this.props.revealed? this.picMap[this.props.value] : (this.props.flagged? flag: base)} className={"Box-logo " +  (this.props.revealed? "Revealed ": "") + (this.props.flagged? "Flagged ": "")} alt={this.props.value} onClick={this.handleClick}/>
        
      );
    }
}
export default GameBody;
