import React from 'react';
import logo from '../images/logo192.png';
import '../App.css';
import { Col, Row, Grid } from 'react-flexbox-grid'

class GameBody extends React.Component {

    constructor(props) {
        super(props);
        this.state = { height: 10, width: 10, mines: 10, flags: {good: 0, bad: 0} };

        this.newGame = this.newGame.bind(this);
        this.updateGameSettings = this.updateGameSettings.bind(this);
        this.createBoxesTable = this.createBoxesTable.bind(this);
        this.createDataTable = this.createDataTable.bind(this);
        this.setMines = this.setMines.bind(this);
        this.applyAround = this.applyAround.bind(this);
        this.increaseValue = this.increaseValue.bind(this);
        this.onClick = this.onClick.bind(this);
        this.fixFlags = this.fixFlags.bind(this);
        //this.boxesTable = this.boxesTable.bind(this);
    }

    newGame() {
        console.log("new Game");
        this.updateGameSettings();
        this.dataTable = this.createDataTable()
        this.setMines();
        this.boxesTable = this.createBoxesTable();
        var id = 0;
        return (
            <div className="Mine-grid">
              {this.boxesTable.map((cols, w) => (
                <Row key={id++} className="Mine-row">
                  {cols.map((box, h) => (
                    <Col key={id++}>
                      {box}
                    </Col>
                  ))}
                </Row>
              ))}
            </div>
        )
                    
    }

    updateGameSettings() {} // to get the new game numbers from the user and set themto the state;

    createDataTable() {
        console.log("create data State");

        var dataTable = Array.from({length: this.state.width}, () => Array.from({length: this.state.height}, () => ({value: 0, isMine: false})))
        // var boxesTable = [];
        // boxesTable.length = this.state.width;

        // var column = [];
        // column.length = this.state.height;
        // //column.fill(new MineBox({click: this.onClick, value: 0, isMine:false}));
        // column.fill(Object.create({value: 0, isMine:false}));
        // boxesTable.fill(column);
        //---------------------------------------------------------
        // for (let w = 0; w < this.state.width; w++) 
        // {
        //     let column = []

        //     column.fill(new MineBox(), 0, height-1);

        //     for (let h = 0; h < this.state.height; h++) 
        //     {
        //         let id = w * this.state.width + h;
        //         childern.push(new MineBox());
        //     }          

        //     this.boxesTable.push(column);

        return dataTable;
    }


    setMines() {
        console.log("set Mines");
        for(let mineCount = 0; mineCount < this.state.mines; mineCount++)
        {
            let x = Math.floor(Math.random() * (this.state.width - 1));
            let y = Math.floor(Math.random() * (this.state.height - 1));
            
            //if(!this.boxesTable[x][y].checkIsMine())
            if(!this.dataTable[x][y].isMine)            
            {
                //this.boxesTable[x][y].setMine()
                this.dataTable[x][y].isMine = true;
                
                this.applyAround({x,y},this.increaseValue)
            }
            else
            {
                mineCount--
            }
        }

    }

    createBoxesTable() {
        var boxesTable = this.dataTable.map((cols, w) => {
            var boxes = cols.map((box, h) => (<MineBox id={w.toString() + "," + h.toString()} click={this.onClick} isMine={box.isMine} value={box.value}></MineBox>))
            return boxes;
        }) 
        console.log(boxesTable);
        return boxesTable;
    }

    applyAround(point,toApplyFunc)
    {        
        console.log("apply Around");
        
        let height = this.state.height;
        let width = this.state.width;
        function checkAndApply(point){// check and apply
            console.log("check and apply");
            
            if(point.x >= 0 && point.y >= 0 && point.x < width && point.y < height) 
            {
                toApplyFunc(point)
            }
        }

        let x = point.x;
        let y = point.y;

        // left side          
        checkAndApply({x: x - 1, y});// <-
        checkAndApply({x: x - 1, y: y - 1});//<-^
        checkAndApply({x: x - 1, y: y + 1})//<-v
          
        // right side
        checkAndApply({x: x + 1, y});// ->
        checkAndApply({x: x + 1, y: y -1});// ->^
        checkAndApply({x: x + 1, y: y + 1})// -> v
          
        // up and down
        checkAndApply({x, y: y - 1});// ^
        checkAndApply({x, y: y + 1})// V
    }

    increaseValue (point) {
        console.log("increase Value");
        this.dataTable[point.x][point.y].value++;
    }

    onClick (shift, box) {
        console.log("on click");
        if (shift)
        {
            this.fixFlags(box)
        }
        else
        {
            if (box.isMine)
            {
                this.gameOver()
            }

            let id = this.convertId(box.id)
            this.reveal(id)
        }
        

    }

    fixFlags(box){}

    reveal(id){
        let {x,y} = id;
        this.boxesTable[x][y].reveal();

        console.log(this.boxesTable[x][y]);

        //if (this.boxesTable[x][y].)

    }

    convertId(id) { 
        if (typeof id == "string")
        {
            let [x,y]= id.split(",");
            return {x,y};
        }
        return id;
    };// to fix if on opposite case arises

    gameOver(){}

    render() {
        
        return (
        <div>
            {this.newGame()}
        </div>
        );
    };
}


class MineBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = { flagged: false , revealed: false};
        this.checkIsMine = this.checkIsMine.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.changeFlagState = this.changeFlagState.bind(this);
        this.reveal = this.reveal.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
    }

    checkIsMine() {
        return this.props.isMine;
    }
   

    changeFlagState() {
        let nextFlagState = !this.state.flagged;
        this.setState((prevState)=> ({flagged: !prevState.flagged}));
        return nextFlagState;
    }

    reveal() {
        this.setState({revealed:true});
    }

    handleClick (e) {
        const isShift = e.shiftKey;
        console.log(e.target);
        let currFlag = this.state.flagged;
        if (isShift)
        {
            currFlag = this.changeFlagState;
        }

        let boxData = {
            isMine: this.props.isMine,
            value: this.props.value,
            flag: currFlag,
            id: this.props.id
        }

        this.props.click(isShift, boxData);

    }

    render() {
      return (
        
          <img src={logo} className="Box-logo" alt={this.props.value} onClick={this.handleClick}/>
        
      );
    }
}
export default GameBody;
